@tailwind base;
@tailwind components;
@tailwind utilities;

/* Import custom animations */
@import "./styles/animations.css";

/* Custom Scrollbar Styles */
@layer utilities {
  .custom-scrollbar {
    scrollbar-width: thin;
    scrollbar-color: hsl(var(--primary) / 0.3) transparent;
  }

  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background-color: hsl(var(--primary) / 0.3);
    border-radius: 20px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background-color: hsl(var(--primary) / 0.5);
  }

  /* Enhanced Glassmorphism Effects */
  .glass-ultra {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.05) 0%, rgba(255, 255, 255, 0.02) 100%);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 
      0 20px 40px rgba(0, 0, 0, 0.2),
      inset 0 1px 1px rgba(255, 255, 255, 0.1);
  }

  .glass-card {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.03) 0%, rgba(255, 255, 255, 0.01) 100%);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 255, 255, 0.08);
  }

  .glass-hover {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-hover:hover {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.08) 0%, rgba(255, 255, 255, 0.03) 100%);
    border-color: rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
    box-shadow: 
      0 25px 50px rgba(0, 0, 0, 0.25),
      inset 0 2px 2px rgba(255, 255, 255, 0.15);
  }

  /* Animated Gradient Background */
  .gradient-animated {
    background: linear-gradient(270deg, #8B5CF6, #3B82F6, #10B981, #F59E0B, #EF4444);
    background-size: 1000% 1000%;
    animation: gradient-shift 15s ease infinite;
  }

  @keyframes gradient-shift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  /* Glow Effects */
  .glow-purple {
    box-shadow: 
      0 0 20px rgba(139, 92, 246, 0.3),
      0 0 40px rgba(139, 92, 246, 0.2),
      0 0 60px rgba(139, 92, 246, 0.1);
  }

  .glow-blue {
    box-shadow: 
      0 0 20px rgba(59, 130, 246, 0.3),
      0 0 40px rgba(59, 130, 246, 0.2),
      0 0 60px rgba(59, 130, 246, 0.1);
  }

  /* Smooth Slide Animations */
  .slide-in-right {
    animation: slideInRight 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes slideInRight {
    from {
      transform: translateX(100%);
      opacity: 0;
    }
    to {
      transform: translateX(0);
      opacity: 1;
    }
  }

  .slide-in-bottom {
    animation: slideInBottom 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  @keyframes slideInBottom {
    from {
      transform: translateY(20px);
      opacity: 0;
    }
    to {
      transform: translateY(0);
      opacity: 1;
    }
  }

  /* Pulse Animation for Live Indicators */
  .pulse-live {
    position: relative;
  }

  .pulse-live::before {
    content: "";
    position: absolute;
    inset: 0;
    border-radius: inherit;
    background: inherit;
    animation: pulse-ring 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse-ring {
    0% {
      transform: scale(1);
      opacity: 1;
    }
    50%, 100% {
      transform: scale(1.2);
      opacity: 0;
    }
  }
}

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 98%;

  --foreground: 0 0% 10%;

  --border: 0 0% 88%;

  --card: 0 0% 96%;

  --card-foreground: 0 0% 10%;

  --card-border: 0 0% 93%;

  --sidebar: 0 0% 94%;

  --sidebar-foreground: 0 0% 10%;

  --sidebar-border: 0 0% 90%;

  --sidebar-primary: 258 90% 66%;

  --sidebar-primary-foreground: 0 0% 98%;

  --sidebar-accent: 258 8% 88%;

  --sidebar-accent-foreground: 258 90% 35%;

  --sidebar-ring: 258 90% 66%;

  --popover: 0 0% 92%;

  --popover-foreground: 0 0% 10%;

  --popover-border: 0 0% 88%;

  --primary: 258 90% 66%;

  --primary-foreground: 0 0% 98%;

  --secondary: 0 0% 90%;

  --secondary-foreground: 0 0% 10%;

  --muted: 258 6% 89%;

  --muted-foreground: 0 0% 45%;

  --accent: 258 8% 90%;

  --accent-foreground: 258 90% 35%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 0 0% 75%;
  --ring: 258 90% 66%;
  --chart-1: 258 90% 35%;
  --chart-2: 280 85% 40%;
  --chart-3: 240 80% 45%;
  --chart-4: 200 75% 40%;
  --chart-5: 180 70% 35%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 1.75rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(258 90% 66% / 0.05);
  --shadow-xs: 0px 2px 4px 0px hsl(258 90% 66% / 0.08);
  --shadow-sm: 0px 3px 6px -1px hsl(258 90% 66% / 0.10), 0px 2px 4px -2px hsl(258 90% 66% / 0.08);
  --shadow: 0px 4px 8px -2px hsl(258 90% 66% / 0.12), 0px 2px 4px -2px hsl(258 90% 66% / 0.08);
  --shadow-md: 0px 6px 12px -2px hsl(258 90% 66% / 0.14), 0px 3px 6px -3px hsl(258 90% 66% / 0.10);
  --shadow-lg: 0px 10px 20px -4px hsl(258 90% 66% / 0.16), 0px 4px 8px -4px hsl(258 90% 66% / 0.12);
  --shadow-xl: 0px 16px 32px -6px hsl(258 90% 66% / 0.18), 0px 6px 12px -6px hsl(258 90% 66% / 0.14);
  --shadow-2xl: 0px 24px 48px -8px hsl(258 90% 66% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

