  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Terminal Mode Specific Colors */
  --terminal-bg: 0 0% 0%;                    /* Pure black */
  --terminal-green: 120 100% 50%;            /* Bright green #00ff00 */
  --terminal-red: 0 100% 50%;                /* Bright red #ff0000 */
  --terminal-amber: 45 100% 50%;             /* Amber/yellow #ffaa00 */
  --terminal-cyan: 180 100% 50%;             /* Cyan for highlights */
  --terminal-white: 0 0% 100%;               /* Pure white */
  --terminal-gray: 0 0% 50%;                 /* Gray for muted text */
  --terminal-border: 0 0% 20%;               /* Dark gray borders */
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {
  /* Glassmorphism effect for luxury cards (Athena spec) */
  .glass {
    background: rgba(255, 255, 255, 0.02);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 0.5px solid rgba(255, 255, 255, 0.08);
  }

  .glass-hover {
    transition: all 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-hover:hover {
    transform: scale(1.02);
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(139, 92, 246, 0.2);
    box-shadow: 0 8px 32px rgba(139, 92, 246, 0.15), 0 0 0 1px rgba(139, 92, 246, 0.1);
  }

  /* Chat message slide-in animation */
  @keyframes slideIn {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-slide-in {
    animation: slideIn 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Purple pulse for loading states */
  @keyframes purplePulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }

  .animate-purple-pulse {
    animation: purplePulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  /* Waveform animation for voice activity */
  @keyframes waveform {
    0%, 100% {
      height: 4px;
    }
    25% {
      height: 16px;
    }
    50% {
      height: 28px;
    }
    75% {
      height: 12px;
    }
  }

  @keyframes waveform-fast {
    0%, 100% {
      height: 8px;
    }
    25% {
      height: 20px;
    }
    50% {
      height: 32px;
    }
    75% {
      height: 14px;
    }
  }

  .animate-waveform {
    animation: waveform 1.5s ease-in-out infinite;
  }

  .animate-waveform-fast {
    animation: waveform-fast 1s ease-in-out infinite;
  }

  /* Thinking dots animation */
  @keyframes thinking {
    0%, 80%, 100% {
      transform: scale(1);
      opacity: 0.5;
    }
    40% {
      transform: scale(1.3);
      opacity: 1;
    }
  }

  .animate-thinking {
    animation: thinking 1.4s ease-in-out infinite;
  }

  /* Thinking dots text effect */
  @keyframes ellipsis {
    0% { content: ""; }
    25% { content: "."; }
    50% { content: ".."; }
    75% { content: "..."; }
    100% { content: ""; }
  }

  .thinking-dots::after {
    content: "";
    animation: ellipsis 1.5s infinite;
  }

  /* Data update glow animation */
  @keyframes dataGlow {
    0% {
      box-shadow: 0 0 0 0 rgba(139, 92, 246, 0.4);
    }
    50% {
      box-shadow: 0 0 15px 5px rgba(139, 92, 246, 0.3);
    }
    100% {
      box-shadow: 0 0 0 0 rgba(139, 92, 246, 0);
    }
  }

  .animate-data-glow {
    animation: dataGlow 800ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Gradient accent glass (use sparingly) */
  .glass-gradient {
    background: linear-gradient(135deg, rgba(139, 92, 246, 0.1), rgba(59, 130, 246, 0.05));
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 0.5px solid rgba(139, 92, 246, 0.2);
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Voice waveform animation */
  @keyframes waveform {
    0%, 100% {
      height: 4px;
    }
    50% {
      height: 24px;
    }
  }

  .animate-waveform {
    animation: waveform 0.8s ease-in-out infinite;
  }

  .animate-waveform-fast {
    animation: waveform 0.4s ease-in-out infinite;
  }

  /* Thinking dots animation */
  @keyframes thinking-dots {
    0%, 20% {
      content: '.';
    }
    40% {
      content: '..';
    }
    60%, 100% {
      content: '...';
    }
  }

  .thinking-dots::after {
    content: '';
    animation: thinking-dots 1.5s infinite;
  }

  /* Avatar Animations - Applied directly to IMG elements */
  @keyframes avatar-breathe {
    0%, 100% { transform: scale(1) translateY(0); }
    50% { transform: scale(1.03) translateY(-2px); }
  }

  @keyframes avatar-talk {
    0%, 100% { transform: scaleY(1); }
    25% { transform: scaleY(0.95); }
    75% { transform: scaleY(1.05); }
  }

  @keyframes avatar-blink {
    0%, 90%, 100% { transform: scaleY(1); }
    95% { transform: scaleY(0.85); }
  }

  @keyframes avatar-head-tilt {
    0%, 100% { transform: rotate(0deg); }
    25% { transform: rotate(-2deg); }
    75% { transform: rotate(2deg); }
  }

  @keyframes listening-pulse {
    0% {
      transform: scale(1);
      opacity: 1;
    }
    100% {
      transform: scale(1.3);
      opacity: 0;
    }
  }

  /* Avatar animation classes */
  .avatar-breathing {
    animation: avatar-breathe 4s ease-in-out infinite;
  }

  .avatar-talking {
    animation: avatar-talk 0.3s ease-in-out infinite;
  }

  .avatar-blink {
    animation: avatar-blink 10s ease-in-out infinite;
  }

  .avatar-head-tilt {
    animation: avatar-head-tilt 8s ease-in-out infinite;
  }

  .animate-listening-pulse {
    animation: listening-pulse 1.5s ease-out infinite;
  }

  .animate-breathe {
    animation: avatar-breathe 4s ease-in-out infinite;
  }

  .animate-talking {
    animation: avatar-talk 0.3s ease-in-out infinite;
  }

  .animate-blink {
    animation: avatar-blink 10s ease-in-out infinite;
  }

  .animate-head-tilt {
    animation: avatar-head-tilt 8s ease-in-out infinite;
  }

  /* Thinking pulse animation */
  @keyframes thinking-pulse {
    0%, 100% {
      opacity: 0.3;
    }
    50% {
      opacity: 1;
    }
  }

  .animate-thinking {
    animation: thinking-pulse 1.5s ease-in-out infinite;
  }

  /* Animation Classes for Enhanced User Experience */
  @keyframes fade-in {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slide-in-from-bottom {
    from { transform: translateY(20px); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }

  @keyframes slide-in-from-top {
    from { transform: translateY(-20px); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }

  @keyframes data-glow {
    0% { box-shadow: 0 0 0 0 rgba(147, 51, 234, 0); }
    50% { box-shadow: 0 0 20px 5px rgba(147, 51, 234, 0.3); }
    100% { box-shadow: 0 0 0 0 rgba(147, 51, 234, 0); }
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  @keyframes scale-in {
    from { transform: scale(0.95); opacity: 0; }
    to { transform: scale(1); opacity: 1; }
  }

  /* Animation utilities */
  .animate-fade-in {
    animation: fade-in 0.5s ease-out;
  }

  .animate-slide-in-from-bottom {
    animation: slide-in-from-bottom 0.5s ease-out;
  }

  .animate-slide-in-from-top {
    animation: slide-in-from-top 0.5s ease-out;
  }

  .animate-data-glow {
    animation: data-glow 0.8s ease-out;
  }

  .animate-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  .animate-scale-in {
    animation: scale-in 0.3s ease-out;
  }

  /* Card hover effects */
  .card-hover {
    transition: all 0.3s ease;
  }

  .card-hover:hover {
    transform: scale(1.02);
    box-shadow: 0 10px 40px rgba(147, 51, 234, 0.15);
  }

  /* Success animation for completed actions */
  @keyframes success-bounce {
    0% { transform: scale(0); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
  }

  .animate-success {
    animation: success-bounce 0.5s ease-out;
  }

  /* Smooth transitions for all interactive elements */
  button, a, input, textarea, select {
    transition: all 0.2s ease;
  }

  /* Focus states for accessibility */
  *:focus-visible {
    outline: 2px solid hsl(var(--primary));
    outline-offset: 2px;
    border-radius: 4px;
  }

  /* Loading skeleton animation */
  @keyframes skeleton-loading {
    0% {
      background-position: -200px 0;
    }
    100% {
      background-position: calc(200px + 100%) 0;
    }
  }

  .skeleton {
    background: linear-gradient(
      90deg,
      hsl(var(--muted) / 0.5) 0%,
      hsl(var(--muted) / 0.2) 50%,
      hsl(var(--muted) / 0.5) 100%
    );
    background-size: 200px 100%;
    animation: skeleton-loading 1.5s ease-in-out infinite;
  }

  /* Avatar Animations - Micro-emotions and breathing */
  @keyframes breathe {
    0%, 100% { 
      transform: scale(1); 
    }
    50% { 
      transform: scale(1.02); 
    }
  }

  @keyframes blink {
    0%, 90%, 100% { 
      opacity: 1; 
    }
    95% { 
      opacity: 0.2; 
    }
  }

  @keyframes subtle-head-tilt {
    0%, 100% { 
      transform: rotate(0deg); 
    }
    25% { 
      transform: rotate(-1deg); 
    }
    75% { 
      transform: rotate(1deg); 
    }
  }

  @keyframes talking {
    0%, 100% {
      transform: scale(1);
    }
    25% {
      transform: scale(1.01);
    }
    50% {
      transform: scale(1.02);
    }
    75% {
      transform: scale(1.01);
    }
  }

  @keyframes listening-pulse {
    0%, 100% {
      box-shadow: 0 0 0 0 hsl(var(--primary) / 0.4);
    }
    50% {
      box-shadow: 0 0 0 20px hsl(var(--primary) / 0);
    }
  }

  /* Avatar animation classes */
  .animate-breathe {
    animation: breathe 3s ease-in-out infinite;
  }

  .animate-blink {
    animation: blink 5s ease-in-out infinite;
  }

  .animate-head-tilt {
    animation: subtle-head-tilt 8s ease-in-out infinite;
  }

  .animate-talking {
    animation: talking 0.5s ease-in-out infinite;
  }

  .animate-listening-pulse {
    animation: listening-pulse 1.5s ease-out infinite;
  }

  /* Thinking dots animation */
  @keyframes thinking {
    0%, 80%, 100% {
      transform: scale(0.8);
      opacity: 0.5;
    }
    40% {
      transform: scale(1.2);
      opacity: 1;
    }
  }

  .animate-thinking {
    animation: thinking 1.4s ease-in-out infinite;
  }
}

/* Critical Avatar Studio fixes to ensure visibility and interaction */
[data-testid="input-avatar-name"],
[data-testid="input-personality-traits"], 
[data-testid="input-visual-style"] {
  background-color: rgba(255,255,255,0.05) !important;
  color: white !important;
  -webkit-text-fill-color: white !important;
  opacity: 1 !important;
}

[data-testid="input-avatar-name"]::placeholder,
[data-testid="input-personality-traits"]::placeholder,
[data-testid="input-visual-style"]::placeholder {
  color: rgba(255,255,255,0.4) !important;
  -webkit-text-fill-color: rgba(255,255,255,0.4) !important;
  opacity: 1 !important;
}

[data-testid="button-create-custom-avatar"] {
  pointer-events: auto !important;
  position: relative !important;
  z-index: 999 !important;
  cursor: pointer !important;
}

