import { useMode } from "@/contexts/ModeContext";
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Layout, MessageCircle, Grid3x3, ChevronDown, LogOut } from "lucide-react";

const MODES = [
  {
    id: "athena" as const,
    name: "Athena Mode",
    description: "Voice-first conversational",
    icon: MessageCircle,
    shortcut: "1",
  },
  {
    id: "hybrid" as const,
    name: "Hybrid Mode",
    description: "Dashboard + Athena",
    icon: Layout,
    shortcut: "2",
  },
  {
    id: "terminal" as const,
    name: "Terminal Mode",
    description: "Multi-panel professional",
    icon: Grid3x3,
    shortcut: "3",
  },
];

export default function ModeSwitcherMenu() {
  const { currentMode, setMode } = useMode();
  const { logout } = useAuth();
  const [, setLocation] = useLocation();

  const handleModeChange = (modeId: "athena" | "hybrid" | "terminal") => {
    setMode(modeId);
    setLocation(`/${modeId}`);
  };

  const handleLogout = async () => {
