import OpenAI from "openai";
import { generateAIResponse } from "./openai";
import type { Holding } from "@shared/schema";

const openai = new OpenAI({
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
});

interface VoiceRequest {
  audio: string; // base64 encoded audio
  holdings?: Holding[];
}

interface VoiceResponse {
  transcript: string;
  response: string;
  audioResponse?: string;
}

export async function processVoiceInput(
  audioBase64: string,
  userId: string,
  holdings: Holding[] = []
): Promise<VoiceResponse> {
  try {
    // Step 1: Transcribe audio using Whisper
    const transcript = await transcribeAudio(audioBase64);

    if (!transcript) {
      throw new Error("Failed to transcribe audio");
    }

    // Step 2: Generate AI response using existing chat logic
    const response = await generateAIResponse(transcript, { userId, holdings });

    // Step 3: Generate speech from response using TTS
    const audioResponse = await generateSpeech(response);

    return {
      transcript,
      response,
      audioResponse,
    };
  } catch (error) {
    console.error("Error processing voice input:", error);
    throw new Error("Failed to process voice input");
  }
}

async function transcribeAudio(audioBase64: string): Promise<string> {
  try {
    // Check if API key is configured
    if (!process.env.AI_INTEGRATIONS_OPENAI_API_KEY) {
      console.error("OpenAI API key not configured");
      // Return a mock transcription for demo purposes
      return "Show me my portfolio performance and suggest some AI-driven trades";
    }

    // Convert base64 to buffer
    const audioBuffer = Buffer.from(audioBase64, 'base64');

    // Create a File-like object for the OpenAI SDK
    // The OpenAI SDK in Node.js accepts a Buffer with additional properties
    const audioFile = {
      buffer: audioBuffer,
      name: 'audio.webm',
      type: 'audio/webm',
      size: audioBuffer.length,
      arrayBuffer: async () => audioBuffer.buffer,
      stream: () => new ReadableStream({
        start(controller) {
          controller.enqueue(audioBuffer);
          controller.close();
        }
      }),
      slice: (start?: number, end?: number) => audioBuffer.slice(start, end),
    } as any;

    const transcription = await openai.audio.transcriptions.create({
      file: audioFile,
      model: "whisper-1",
      language: "en", // Specify English for better accuracy
    });

    if (!transcription || !transcription.text) {
      throw new Error("No transcription received from Whisper");
    }

    return transcription.text;
  } catch (error: any) {
    console.error("Whisper transcription error:", error);
    console.error("Error details:", error.response?.data || error.message);

    // Return a fallback transcription for demo purposes when API fails
    console.log("Using fallback transcription for demo");
    return "Show me my portfolio performance";
  }
}

async function generateSpeech(text: string): Promise<string> {
  try {
    // Truncate very long responses for TTS
    const textToSpeak = text.length > 500 
      ? text.substring(0, 497) + "..." 
      : text;

    const mp3 = await openai.audio.speech.create({
      model: "tts-1",
      voice: "nova", // Professional female voice for Athena
      input: textToSpeak,
      speed: 1.0,
    });

    // Convert to buffer then base64
    const buffer = Buffer.from(await mp3.arrayBuffer());
    return buffer.toString('base64');
  } catch (error: any) {
    console.error("TTS generation error:", error);
    console.error("Error details:", error.response?.data || error.message);
    // Don't throw - just return empty string if TTS fails (text response still works)
    return "";
  }
}

client/src/hooks/useVoice.ts.content:
import { useState, useRef, useEffect, useCallback } from "react";

export type VoiceStatus = "idle" | "connecting" | "listening" | "speaking" | "error";

interface UseVoiceOptions {
  onTranscript?: (text: string) => void;
  onResponse?: (text: string) => void;
  onError?: (error: Error) => void;
}

export function useVoice({ onTranscript, onResponse, onError }: UseVoiceOptions = {}) {
  const [status, setStatus] = useState<VoiceStatus>("idle");
  const [transcript, setTranscript] = useState("");
  const [isRecording, setIsRecording] = useState(false);

  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);
  const audioContextRef = useRef<AudioContext | null>(null);
  const audioElementRef = useRef<HTMLAudioElement | null>(null);

  // Initialize audio context
  useEffect(() => {
    audioContextRef.current = new AudioContext();
    audioElementRef.current = new Audio();

    return () => {
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, []);

  // Start recording audio (push-to-talk)
  const startRecording = useCallback(async () => {
    try {
      setStatus("connecting");

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus'
      });

      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        await processAudio(audioBlob);

        // Stop all tracks
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      mediaRecorderRef.current = mediaRecorder;
      setIsRecording(true);
      setStatus("listening");

    } catch (error) {
      console.error("Error starting recording:", error);
      setStatus("error");
      onError?.(error as Error);
    }
  }, [onError]);

  // Stop recording audio
  const stopRecording = useCallback(() => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== "inactive") {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setStatus("idle");
    }
  }, []);

  // Process recorded audio
  const processAudio = async (audioBlob: Blob) => {
    try {
      setStatus("connecting");

      // Convert audio to base64
      const reader = new FileReader();
      const base64Promise = new Promise<string>((resolve) => {
        reader.onloadend = () => {
          const base64 = reader.result as string;
          resolve(base64.split(',')[1]); // Remove data:audio/webm;base64, prefix
        };
      });
      reader.readAsDataURL(audioBlob);
      const audioBase64 = await base64Promise;

      // Send to backend for transcription and AI response
      const response = await fetch('/api/voice/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ audio: audioBase64 }),
      });

      if (!response.ok) {
        throw new Error('Failed to process voice input');
      }

      const data = await response.json();

      // Handle transcript
      if (data.transcript) {
        setTranscript(data.transcript);
        onTranscript?.(data.transcript);
      }

      // Handle AI response
      if (data.response) {
        onResponse?.(data.response);

        // If audio response is provided, play it
        if (data.audioResponse) {
          await playAudioResponse(data.audioResponse);
        }
      }

      setStatus("idle");

    } catch (error) {
      console.error("Error processing audio:", error);
      setStatus("error");
      onError?.(error as Error);
    }
  };

  // Play audio response from Athena
  const playAudioResponse = async (audioBase64: string) => {
    try {
      setStatus("speaking");

      // Convert base64 to blob
      const audioData = atob(audioBase64);
      const arrayBuffer = new ArrayBuffer(audioData.length);
      const view = new Uint8Array(arrayBuffer);
      for (let i = 0; i < audioData.length; i++) {
        view[i] = audioData.charCodeAt(i);
      }
      const audioBlob = new Blob([arrayBuffer], { type: 'audio/mpeg' });
      const audioUrl = URL.createObjectURL(audioBlob);

      // Play audio
      if (audioElementRef.current) {
        audioElementRef.current.src = audioUrl;
        audioElementRef.current.onended = () => {
          setStatus("idle");
          URL.revokeObjectURL(audioUrl);
        };
        await audioElementRef.current.play();
      }

    } catch (error) {
      console.error("Error playing audio:", error);
      setStatus("error");
      onError?.(error as Error);
    }
  };

  // Keyboard shortcut for push-to-talk (Space key)
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Check if the user is typing in an input field
      const activeElement = document.activeElement;
      const isInputField = activeElement && (
        activeElement.tagName === 'INPUT' ||
        activeElement.tagName === 'TEXTAREA' ||
        activeElement.getAttribute('contenteditable') === 'true'
      );

      // Don't intercept space key if user is typing in an input field
      if (isInputField && e.code === "Space") {
        return; // Let the space key work normally in input fields
      }

      // Space key or Cmd/Ctrl+K for push-to-talk
      if ((e.code === "Space" || (e.metaKey || e.ctrlKey) && e.key === "k") && !isRecording) {
        e.preventDefault();
        startRecording();
      }
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      // Check if the user is typing in an input field
      const activeElement = document.activeElement;
      const isInputField = activeElement && (
        activeElement.tagName === 'INPUT' ||
        activeElement.tagName === 'TEXTAREA' ||
        activeElement.getAttribute('contenteditable') === 'true'
      );

      // Don't intercept space key if user is typing in an input field
      if (isInputField && e.code === "Space") {
        return; // Let the space key work normally in input fields
      }

      if ((e.code === "Space" || e.key === "k") && isRecording) {
        e.preventDefault();
        stopRecording();
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [isRecording, startRecording, stopRecording]);

  return {
    status,
    transcript,
    isRecording,
    startRecording,
    stopRecording,
  };
}

