    sentimentLabel: "Very Bullish",
  },
];

/**
 * Get all market news (with 5-minute caching)
 */
const getMarketNewsUncached = async (): Promise<NewsArticle[]> => {
  // TODO: Replace with Alpha Vantage API when API key is available
  // const response = await fetch(
  //   `https://www.alphavantage.co/query?function=NEWS_SENTIMENT&apikey=${process.env.ALPHA_VANTAGE_API_KEY}`
  // );

  return mockNews;
};

export const getMarketNews = memoize(getMarketNewsUncached, { 
  promise: true,
  maxAge: 5 * 60 * 1000, // 5 minutes
});

/**
 * Get news for specific tickers
 */
export const getNewsForTickers = async (tickers: string[]): Promise<NewsArticle[]> => {
  const allNews = await getMarketNews();

  return allNews.filter(article => 
    article.tickers && article.tickers.some(ticker => 
      tickers.includes(ticker)
    )
  );
};

/**
 * Get a single news article by ID
 */
export const getNewsArticleById = async (id: string): Promise<NewsArticle | null> => {
  const allNews = await getMarketNews();
  return allNews.find(article => article.id === id) || null;
};

client/src/pages/SettingsPage.tsx.content:
import { useState } from "react";
import { useAuth, ProtectedRoute } from "@/lib/auth";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiJson, queryClient } from "@/lib/queryClient";
import { DollarSign, User, Bell, Lock } from "lucide-react";
import Navigation from "@/components/Navigation";
import NavigationBreadcrumbs from "@/components/NavigationBreadcrumbs";
import BackButton from "@/components/BackButton";

function SettingsPageContent() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [isAddingFunds, setIsAddingFunds] = useState(false);
  const [isUpdatingProfile, setIsUpdatingProfile] = useState(false);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [fundAmount, setFundAmount] = useState("");

  const [profile, setProfile] = useState({
    fullName: user?.username || "",
    phone: "",
    emailNotifications: true,
    pushNotifications: false,
  });

  const [passwords, setPasswords] = useState({
    currentPassword: "",
    newPassword: "",
  });

  const handleUpdateProfile = async () => {
    setIsUpdatingProfile(true);
    try {
      // Only send fields backend expects
      await apiJson("PATCH", "/api/user/profile", {
        fullName: profile.fullName,
        phone: profile.phone,
      });
      toast({
        title: "Profile Updated",
        description: "Your profile has been updated successfully",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    } finally {
      setIsUpdatingProfile(false);
    }
  };

  const handleAddFunds = async () => {
    const amount = parseFloat(fundAmount);
    if (!fundAmount || !Number.isFinite(amount) || amount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a valid positive number",
        variant: "destructive",
      });
      return;
    }

    setIsAddingFunds(true);
    try {
      await apiJson("POST", "/api/account/deposit", {
        amount: fundAmount,
      });

      // Invalidate portfolio queries to refresh balance
      queryClient.invalidateQueries({ queryKey: ['/api/portfolio/summary'] });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });

      toast({
        title: "Funds Added",
        description: `$${fundAmount} has been added to your account`,
      });
      setFundAmount("");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to process deposit",
        variant: "destructive",
      });
    } finally {
      setIsAddingFunds(false);
    }
  };

  const handleChangePassword = async () => {
    if (!passwords.currentPassword || !passwords.newPassword) {
      toast({
        title: "Missing Information",
        description: "Please fill in all password fields",
        variant: "destructive",
      });
      return;
    }

    if (passwords.newPassword.length < 6) {
      toast({
        title: "Invalid Password",
        description: "New password must be at least 6 characters",
        variant: "destructive",
      });
      return;
    }

    setIsChangingPassword(true);
    try {
      await apiJson("PATCH", "/api/user/password", {
        currentPassword: passwords.currentPassword,
        newPassword: passwords.newPassword,
      });

      toast({
        title: "Password Changed",
        description: "Your password has been updated successfully",
      });
      setPasswords({ currentPassword: "", newPassword: "" });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to change password",
        variant: "destructive",
      });
    } finally {
      setIsChangingPassword(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-foreground">
      <Navigation />
      <NavigationBreadcrumbs />
      <div className="max-w-[1200px] mx-auto px-6 sm:px-10 lg:px-16 py-8 lg:py-12">
        {/* Header */}
        <div className="mb-12">
          <div className="mb-4">
            <BackButton />
          </div>
          <h1 className="text-5xl md:text-6xl lg:text-7xl font-extralight mb-4">
            Settings
          </h1>
          <p className="text-lg text-muted-foreground font-light">
            Manage your account and preferences
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Account Funding */}
          <Card className="bg-card border-white/10 rounded-[28px]" data-testid="card-funding">
            <CardHeader>
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 rounded-full bg-primary/20 flex items-center justify-center">
                  <DollarSign className="w-6 h-6 text-primary" />
                </div>
                <div>
                  <CardTitle className="text-2xl font-light">Add Funds</CardTitle>
                  <CardDescription className="mt-1">Deposit money to your account</CardDescription>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="amount" className="text-foreground">Amount (USD)</Label>
                <Input
                  id="amount"
                  type="number"
                  step="0.01"
                  value={fundAmount}
                  onChange={(e) => setFundAmount(e.target.value)}
                  placeholder="1000.00"
