import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { ProtectedRoute } from "@/lib/auth";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiJson, queryClient } from "@/lib/queryClient";
import { Check, X, TrendingUp, TrendingDown, Clock, Sparkles } from "lucide-react";
import type { Trade } from "@shared/schema";
import Navigation from "@/components/Navigation";
import NavigationBreadcrumbs from "@/components/NavigationBreadcrumbs";
import BackButton from "@/components/BackButton";
import { TickerLink } from "@/components/TickerLink";

function TradesPageContent() {
  const { toast } = useToast();
  const [filter, setFilter] = useState<"all" | "pending" | "executed">("pending");

  const { data: allTrades = [], isLoading } = useQuery<Trade[]>({
    queryKey: ["/api/trades"],
  });

  const approveTrade = useMutation({
    mutationFn: async (tradeId: string) => {
      return apiJson("PATCH", `/api/trades/${tradeId}/status`, { status: "approved" });
    },
    onSuccess: () => {
      toast({
        title: "Trade Approved",
        description: "The trade has been approved and will be executed",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/trades"] });
      queryClient.invalidateQueries({ queryKey: ["/api/holdings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/portfolio/summary"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to approve trade",
        variant: "destructive",
      });
    },
  });

  const rejectTrade = useMutation({
    mutationFn: async (tradeId: string) => {
      return apiJson("PATCH", `/api/trades/${tradeId}/status`, { status: "rejected" });
    },
    onSuccess: () => {
      toast({
        title: "Trade Rejected",
        description: "The trade has been rejected",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/trades"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reject trade",
        variant: "destructive",
      });
    },
  });

  const generateSuggestions = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/ai/trade-suggestions", {
        credentials: "include",
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || "Failed to generate suggestions");
      }
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "AI Suggestions Generated",
        description: `Created ${data.length} new trade suggestions for your review`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/trades"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to generate AI suggestions",
        variant: "destructive",
      });
    },
  });

  const filteredTrades = allTrades.filter(trade => {
    if (filter === "all") return true;
    if (filter === "pending") return trade.status === "pending" || trade.status === "approved";
    if (filter === "executed") return trade.status === "executed";
    return true;
  });

  const formatCurrency = (value: string) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
    }).format(parseFloat(value));
  };

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getStatusBadge = (status: string) => {
    const variants: Record<string, { variant: "default" | "secondary" | "destructive" | "outline", label: string }> = {
      pending: { variant: "secondary", label: "Pending" },
      approved: { variant: "default", label: "Approved" },
      executed: { variant: "outline", label: "Executed" },
      rejected: { variant: "destructive", label: "Rejected" },
    };
    const { variant, label } = variants[status] || { variant: "outline", label: status };
    return <Badge variant={variant} data-testid={`badge-status-${status}`}>{label}</Badge>;
  };

  return (
    <div className="min-h-screen bg-black text-foreground">
      <Navigation />
      <NavigationBreadcrumbs />
      <div className="max-w-[1600px] mx-auto px-6 sm:px-10 lg:px-16 py-8 lg:py-12">
        {/* Header */}
        <div className="mb-12">
          <div className="mb-4">
            <BackButton />
          </div>
          <div className="flex flex-wrap items-start justify-between gap-4 mb-4">
            <div>
              <h1 className="text-5xl md:text-6xl lg:text-7xl font-extralight mb-2">
                Trades
              </h1>
              <p className="text-lg text-muted-foreground font-light">
                Review and manage your trade suggestions
              </p>
            </div>
            <Button
              onClick={() => generateSuggestions.mutate()}
              disabled={generateSuggestions.isPending}
              className="rounded-full mt-4"
              size="lg"
              data-testid="button-generate-suggestions"
            >
              <Sparkles className="w-4 h-4 mr-2" />
              {generateSuggestions.isPending ? "Generating..." : "Get AI Suggestions"}
            </Button>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="flex gap-2 mb-8">
          <Button
            variant={filter === "pending" ? "default" : "ghost"}
            onClick={() => setFilter("pending")}
            className="rounded-full"
            data-testid="button-filter-pending"
          >
            Pending
          </Button>
          <Button
            variant={filter === "executed" ? "default" : "ghost"}
            onClick={() => setFilter("executed")}
            className="rounded-full"
            data-testid="button-filter-executed"
          >
            Executed
          </Button>
          <Button
            variant={filter === "all" ? "default" : "ghost"}
            onClick={() => setFilter("all")}
            className="rounded-full"
            data-testid="button-filter-all"
          >
            All
          </Button>
        </div>

        {/* Trades List */}
        {isLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground font-light">Loading trades...</p>
          </div>
        ) : filteredTrades.length === 0 ? (
          <Card className="bg-card border-white/10 rounded-[28px]">
            <CardContent className="text-center py-12">
              <Clock className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground font-light mb-2">No {filter === "all" ? "" : filter} trades</p>
              <p className="text-sm text-muted-foreground">
                Ask Athena for trade suggestions to get started
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {filteredTrades.map((trade) => (
              <Card 
                key={trade.id} 
                className="bg-card border-white/10 rounded-[28px]"
                data-testid={`trade-card-${trade.id}`}
              >
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div className="flex items-center gap-4">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
                        trade.type === 'buy' ? 'bg-primary/20' : 'bg-destructive/20'
                      }`}>
                        {trade.type === 'buy' ? 
                          <TrendingUp className="w-6 h-6 text-primary" /> : 
                          <TrendingDown className="w-6 h-6 text-destructive" />
                        }
                      </div>
                      <div>
                        <div className="flex items-center gap-3 mb-1">
                          <CardTitle className="text-2xl font-light">
                            <TickerLink symbol={trade.symbol} />
                          </CardTitle>
                          <Badge variant={trade.type === 'buy' ? "default" : "destructive"}>
                            {trade.type.toUpperCase()}
                          </Badge>
                        </div>
                        <CardDescription>
                          {parseFloat(trade.quantity).toFixed(2)} shares @ {formatCurrency(trade.price)}
                        </CardDescription>
                      </div>
                    </div>
                    <div className="text-right">
                      {getStatusBadge(trade.status)}
                      <p className="text-xs text-muted-foreground mt-1">
                        {formatDate(trade.createdAt)}
                      </p>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {trade.reasoning && (
                    <div>
                      <p className="text-sm text-muted-foreground mb-2">AI Reasoning</p>
                      <p className="text-base text-foreground leading-relaxed">{trade.reasoning}</p>
                    </div>
                  )}

                  {trade.confidence && (
                    <div>
                      <p className="text-sm text-muted-foreground mb-1">Confidence</p>
                      <p className="text-lg font-light text-primary">{parseFloat(trade.confidence).toFixed(0)}%</p>
                    </div>
                  )}

                  <div className="flex items-center justify-between pt-2 border-t border-white/5">
                    <p className="text-sm font-medium text-foreground">
                      Total: {formatCurrency((parseFloat(trade.quantity) * parseFloat(trade.price)).toFixed(2))}
                    </p>

                    {trade.status === "pending" && (
                      <div className="flex gap-3">
                        <Button
                          onClick={() => approveTrade.mutate(trade.id)}
                          disabled={approveTrade.isPending || rejectTrade.isPending}
                          className="rounded-full"
                          data-testid={`button-approve-${trade.id}`}
                        >
                          <Check className="w-4 h-4 mr-2" />
                          Approve
                        </Button>
                        <Button
                          onClick={() => rejectTrade.mutate(trade.id)}
                          disabled={approveTrade.isPending || rejectTrade.isPending}
                          variant="outline"
                          className="rounded-full"
                          data-testid={`button-reject-${trade.id}`}
                        >
                          <X className="w-4 h-4 mr-2" />
                          Decline
                        </Button>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default function TradesPage() {
  return (
    <ProtectedRoute>
      <TradesPageContent />
    </ProtectedRoute>
  );
}

client/src/pages/AnalyticsPage.tsx.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, TrendingDown, AlertTriangle, Activity, BarChart3, Network, Zap } from "lucide-react";
import { ProtectedRoute } from "@/lib/auth";
import RiskInsights from "@/components/RiskInsights";
import Navigation from "@/components/Navigation";
import NavigationBreadcrumbs from "@/components/NavigationBreadcrumbs";
import BackButton from "@/components/BackButton";
import { TickerLink } from "@/components/TickerLink";
import { AdvancedAnalytics } from "@/components/AdvancedAnalytics";

interface CorrelationData {
  pairs: Array<{
    symbol1: string;
    symbol2: string;
    correlation: number;
  }>;
  concentrationRisk: number;
}

interface FactorExposure {
  factor: string;
  exposure: number;
  description: string;
}

interface MarketRegime {
  regime: 'bull' | 'bear' | 'high-volatility' | 'neutral';
  confidence: number;
  description: string;
  vix: number;
  marketTrend: number;
}

interface StressTestResult {
  scenario: string;
  portfolioImpact: number;
  description: string;
  year?: string;
}

function AnalyticsPageContent() {
  const { data: correlations, isLoading: loadingCorr } = useQuery<CorrelationData>({
    queryKey: ['/api/analytics/correlation'],
  });

  const { data: factors, isLoading: loadingFactors } = useQuery<FactorExposure[]>({
    queryKey: ['/api/analytics/factors'],
  });

  const { data: regime, isLoading: loadingRegime } = useQuery<MarketRegime>({
    queryKey: ['/api/analytics/regime'],
  });

  const { data: stressTests, isLoading: loadingStress } = useQuery<StressTestResult[]>({
    queryKey: ['/api/analytics/stress-test'],
  });

  const isLoading = loadingCorr || loadingFactors || loadingRegime || loadingStress;

  return (
    <div className="min-h-screen bg-black">
      <Navigation />
      <NavigationBreadcrumbs />
      <div className="max-w-7xl mx-auto p-6 space-y-6">
        {/* Header */}
        <div>
          <div className="mb-4">
            <BackButton />
          </div>
          <div className="space-y-2">
            <h1 className="text-4xl font-extralight text-foreground">
              Institutional Analytics
            </h1>
            <p className="text-muted-foreground font-light">
              Advanced portfolio analysis and risk insights
            </p>
          </div>
        </div>

        {/* Advanced Performance & Risk Analytics */}
        <div className="mb-8">
          <AdvancedAnalytics />
        </div>

        {/* Correlation Analysis */}
        <Card className="rounded-[28px]">
          <CardHeader>
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-lg bg-primary/10">
                <Network className="w-5 h-5 text-primary" />
              </div>
              <div>
                <CardTitle className="text-xl font-light">Correlation Analysis</CardTitle>
                <CardDescription>
                  See how your holdings move together in real-time
                </CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {loadingCorr ? (
              <div className="space-y-3">
                <Skeleton className="h-20 w-full" />
                <Skeleton className="h-20 w-full" />
              </div>
            ) : correlations && correlations.pairs.length > 0 ? (
              <div className="space-y-4">
                <div className="grid gap-3">
                  {correlations.pairs.map((pair, idx) => (
                    <div
                      key={idx}
                      className="flex items-center justify-between p-4 rounded-lg bg-card/50 hover-elevate"
                      data-testid={`correlation-pair-${idx}`}
                    >
                      <div className="flex items-center gap-3">
                        <span className="font-light text-foreground flex items-center gap-2">
                          <TickerLink symbol={pair.symbol1} />
                          <span className="text-muted-foreground"></span>
                          <TickerLink symbol={pair.symbol2} />
                        </span>
                        <Badge
                          variant={Math.abs(pair.correlation) > 0.7 ? "default" : "secondary"}
                          className="no-default-hover-elevate"
                        >
                          {Math.abs(pair.correlation) > 0.7 ? "High" : "Moderate"}
                        </Badge>
                      </div>
                      <div className="flex items-center gap-2">
                        <div className="w-32 h-2 bg-muted rounded-full overflow-hidden">
                          <div
                            className="h-full bg-primary rounded-full"
                            style={{ width: `${Math.abs(pair.correlation) * 100}%` }}
                          />
                        </div>
                        <span className="text-sm font-light text-muted-foreground w-12 text-right">
                          {(pair.correlation * 100).toFixed(0)}%
                        </span>
                      </div>
                    </div>
                  ))}
                </div>

                {correlations.concentrationRisk > 0.7 && (
                  <div className="flex items-start gap-3 p-4 rounded-lg bg-destructive/10 border border-destructive/20">
                    <AlertTriangle className="w-5 h-5 text-destructive mt-0.5" />
                    <div>
                      <p className="font-light text-destructive">High Concentration Risk</p>
                      <p className="text-sm text-destructive/80 font-light mt-1">
                        Your portfolio has multiple holdings that move together (correlation{' '}
                        {(correlations.concentrationRisk * 100).toFixed(0)}%). Consider diversification.
                      </p>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <p className="text-muted-foreground font-light text-center py-8">
                Add multiple holdings to see correlation analysis
              </p>
            )}
          </CardContent>
        </Card>

        {/* Factor Insights */}
        <Card className="rounded-[28px]">
          <CardHeader>
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-lg bg-primary/10">
                <BarChart3 className="w-5 h-5 text-primary" />
              </div>
              <div>
                <CardTitle className="text-xl font-light">Factor Insights</CardTitle>
                <CardDescription>
                  What's really driving your returns
                </CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {loadingFactors ? (
              <div className="space-y-3">
                <Skeleton className="h-20 w-full" />
                <Skeleton className="h-20 w-full" />
                <Skeleton className="h-20 w-full" />
              </div>
            ) : factors && factors.length > 0 ? (
              <div className="space-y-4">
                {factors.map((factor, idx) => (
                  <div
                    key={idx}
                    className="p-4 rounded-lg bg-card/50 hover-elevate"
                    data-testid={`factor-${factor.factor.toLowerCase()}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-3">
                        <span className="font-light text-foreground">{factor.factor}</span>
                        <Badge
                          variant={Math.abs(factor.exposure) > 0.6 ? "default" : "secondary"}
                          className="no-default-hover-elevate"
                        >
                          {Math.abs(factor.exposure) > 0.6 ? "Strong" : "Moderate"}
                        </Badge>
                      </div>
                      <span className="text-sm font-light text-muted-foreground">
                        {(factor.exposure * 100).toFixed(0)}% exposure
                      </span>
                    </div>
                    <div className="w-full h-2 bg-muted rounded-full overflow-hidden mb-2">
                      <div
                        className="h-full bg-primary rounded-full"
                        style={{ width: `${Math.abs(factor.exposure) * 100}%` }}
                      />
                    </div>
                    <p className="text-sm text-muted-foreground font-light">
                      {factor.description}
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-muted-foreground font-light text-center py-8">
                Build your portfolio to see factor analysis
              </p>
            )}
          </CardContent>
        </Card>

        {/* Market Regime Tracking */}
        <Card className="rounded-[28px]">
          <CardHeader>
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-lg bg-primary/10">
                <Activity className="w-5 h-5 text-primary" />
              </div>
              <div>
                <CardTitle className="text-xl font-light">Market Regime</CardTitle>
                <CardDescription>
                  Current market environment and recommended strategies
                </CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {loadingRegime ? (
              <Skeleton className="h-32 w-full" />
            ) : regime ? (
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    {regime.regime === 'bull' && <TrendingUp className="w-6 h-6 text-green-500" />}
                    {regime.regime === 'bear' && <TrendingDown className="w-6 h-6 text-red-500" />}
                    {regime.regime === 'high-volatility' && <Zap className="w-6 h-6 text-yellow-500" />}
                    {regime.regime === 'neutral' && <Activity className="w-6 h-6 text-muted-foreground" />}
                    <div>
                      <h3 className="text-2xl font-light text-foreground capitalize">
                        {regime.regime.replace('-', ' ')} Market
                      </h3>
                      <p className="text-sm text-muted-foreground font-light">
                        {regime.confidence}% confidence
                      </p>
                    </div>
                  </div>
                  <Badge
                    variant="outline"
                    className="text-lg px-4 py-2 no-default-hover-elevate"
                    data-testid="regime-badge"
                  >
                    VIX: {regime.vix.toFixed(1)}
                  </Badge>
                </div>
                <p className="text-muted-foreground font-light">
                  {regime.description}
                </p>
                <div className="grid grid-cols-2 gap-4 p-4 rounded-lg bg-card/50">
                  <div>
                    <p className="text-sm text-muted-foreground font-light mb-1">Market Trend</p>
                    <p className="text-lg font-light text-foreground">
                      {regime.marketTrend > 0 ? '+' : ''}{(regime.marketTrend * 100).toFixed(1)}%
                    </p>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground font-light mb-1">Volatility (VIX)</p>
                    <p className="text-lg font-light text-foreground">{regime.vix.toFixed(2)}</p>
                  </div>
                </div>
              </div>
            ) : (
              <p className="text-muted-foreground font-light text-center py-8">
                Market regime data unavailable
              </p>
            )}
          </CardContent>
        </Card>

        {/* Stress Testing */}
        <Card className="rounded-[28px]">
          <CardHeader>
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-lg bg-primary/10">
                <AlertTriangle className="w-5 h-5 text-primary" />
              </div>
              <div>
                <CardTitle className="text-xl font-light">Stress Testing</CardTitle>
                <CardDescription>
                  How your portfolio would perform in past crises
                </CardDescription>
              </div>
            </div>
          </CardHeader>
          <CardContent>
            {loadingStress ? (
              <div className="space-y-3">
                <Skeleton className="h-24 w-full" />
                <Skeleton className="h-24 w-full" />
                <Skeleton className="h-24 w-full" />
              </div>
            ) : stressTests && stressTests.length > 0 ? (
              <div className="space-y-4">
                {stressTests.map((test, idx) => (
                  <div
                    key={idx}
                    className="p-4 rounded-lg bg-card/50 hover-elevate"
                    data-testid={`stress-test-${test.scenario.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div>
                        <h4 className="font-light text-foreground">{test.scenario}</h4>
                        {test.year && (
                          <p className="text-sm text-muted-foreground font-light">{test.year}</p>
                        )}
                      </div>
                      <div className="text-right">
                        <p
                          className={`text-2xl font-light ${
                            test.portfolioImpact < 0 ? 'text-destructive' : 'text-green-500'
                          }`}
                        >
                          {test.portfolioImpact > 0 ? '+' : ''}
                          {test.portfolioImpact.toFixed(1)}%
                        </p>
                      </div>
                    </div>
                    <p className="text-sm text-muted-foreground font-light">
                      {test.description}
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-muted-foreground font-light text-center py-8">
                Add holdings to see stress test results
              </p>
            )}
          </CardContent>
        </Card>

        {/* Risk Insights */}
        <div className="space-y-2 mb-6">
          <h2 className="text-3xl font-extralight text-foreground">
            Portfolio Risk Analysis
          </h2>
          <p className="text-muted-foreground font-light">
            Comprehensive risk metrics and alerts
          </p>
        </div>
        <RiskInsights />
      </div>
    </div>
  );
}

export default function AnalyticsPage() {
  return (
    <ProtectedRoute>
      <AnalyticsPageContent />
    </ProtectedRoute>
  );
}

