                </Button>
              </div>
            </SheetContent>
          </Sheet>
          </div>
        </div>
      </div>

      {/* Avatar Studio Modal */}
      <AvatarStudio open={avatarStudioOpen} onClose={() => setAvatarStudioOpen(false)} />
    </nav>
  );
}

