interface UnifiedHeaderProps {
  showModeSwitcher?: boolean;
  transparentBg?: boolean;
  className?: string;
}

export default function UnifiedHeader({ 
  showModeSwitcher = true, 
  transparentBg = false,
  className = "" 
}: UnifiedHeaderProps) {
