  const { currentMode } = useMode();
  const { logout, user } = useAuth();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Fetch active avatar data
  const { data: activeAvatar } = useQuery<{
    name: string;
    imageUrl: string;
    personalityProfile: {
      catchphrase?: string;
      [key: string]: any;
    };
  }>({
    queryKey: ['/api/avatars/active'],
