import { useState, useRef } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { User, Sparkles, Check, Upload, Image as ImageIcon } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface AvatarStudioProps {
  open: boolean;
  onClose: () => void;
}

export default function AvatarStudio({ open, onClose }: AvatarStudioProps) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [personality, setPersonality] = useState("");
  const [tradingStyle, setTradingStyle] = useState("balanced");
  const [appearance, setAppearance] = useState("");
  const [avatarImage, setAvatarImage] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");
  const [isDragging, setIsDragging] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  // Fetch preset avatars from API
  const { data: presets = [], isLoading: presetsLoading } = useQuery<any[]>({
    queryKey: ['/api/avatars/presets'],
    enabled: open,
  });

  // Fetch user's avatar history
  const { data: history = [] } = useQuery<any[]>({
    queryKey: ['/api/avatars/history'],
    enabled: open,
  });

  // Select avatar mutation - use personaKey for presets
  const selectAvatar = useMutation({
    mutationFn: async (identifier: string) => {
      const response = await apiRequest('POST', `/api/avatars/${identifier}/select`);
      if (!response.ok) throw new Error('Failed to select avatar');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user'] });
      queryClient.invalidateQueries({ queryKey: ['/api/avatars/history'] });
      onClose();
    }
  });

  // Process file (shared between select and drop)
  const processFile = (file: File) => {
    // Check file type
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid file type",
        description: "Please select an image file (JPEG, PNG, GIF, etc.)",
        variant: "destructive",
      });
      return;
    }

    // Check file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please select an image smaller than 5MB",
        variant: "destructive",
      });
      return;
    }

    setAvatarImage(file);

    // Create preview
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result as string);
    };
    reader.readAsDataURL(file);
  };

  // Handle file selection
  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      processFile(file);
    }
  };

  // Drag and drop handlers
  const handleDragEnter = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();

    // Only set dragging to false if we're leaving the actual drop zone
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX;
    const y = e.clientY;

    if (x < rect.left || x >= rect.right || y < rect.top || y >= rect.bottom) {
      setIsDragging(false);
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      processFile(files[0]);
    }
  };

  // Create custom avatar mutation with image upload
  const createCustom = useMutation({
    mutationFn: async (data: any) => {
      const formData = new FormData();
      // Combine first and last name, or use just first name
      const fullName = data.lastName ? `${data.firstName} ${data.lastName}` : data.firstName;
      formData.append('name', fullName);
      formData.append('firstName', data.firstName);
      formData.append('lastName', data.lastName || '');
      formData.append('personality', data.personality);
      formData.append('tradingStyle', data.tradingStyle);
      formData.append('appearance', data.appearance || '');

      if (avatarImage) {
        formData.append('avatar', avatarImage);
      }

      const response = await fetch('/api/avatars/custom', {
        method: 'POST',
        credentials: 'include',
        body: formData,
      });

      if (!response.ok) throw new Error('Failed to create custom avatar');
      return response.json();
    },
    onSuccess: (avatar) => {
      queryClient.invalidateQueries({ queryKey: ['/api/avatars/active'] });
      setFirstName("");
      setLastName("");
      setPersonality("");
      setTradingStyle("balanced");
      setAppearance("");
      setAvatarImage(null);
      setImagePreview("");
      if (fileInputRef.current) {
        fileInputRef.current.value = "";
      }
      selectAvatar.mutate(avatar.id);
      toast({
        title: "Avatar created!",
        description: "Your custom avatar has been created and selected.",
      });
    }
  });

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent 
        className="max-w-4xl max-h-[80vh] overflow-y-auto !bg-black border-white/10 !text-white" 
        style={{ 
          zIndex: 9999,
          backgroundColor: '#000000',
          color: 'white',
          pointerEvents: 'auto'
        }}>
        <DialogHeader>
          <DialogTitle className="text-2xl font-semibold">
            Avatar Studio
          </DialogTitle>
        </DialogHeader>

        <Tabs defaultValue="presets" className="mt-6">
          <TabsList className="grid grid-cols-3 w-full bg-white/5">
            <TabsTrigger value="presets" data-testid="tab-preset-avatars">Preset Avatars</TabsTrigger>
            <TabsTrigger value="custom" data-testid="tab-create-custom">Create Custom</TabsTrigger>
            <TabsTrigger value="history" data-testid="tab-my-avatars">My Avatars</TabsTrigger>
          </TabsList>

          <TabsContent value="presets" className="space-y-4 mt-6">
            {presetsLoading ? (
              <div className="flex justify-center py-8">
                <div className="text-muted-foreground">Loading avatars...</div>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {presets?.map((avatar: any) => (
                  <Card 
                    key={avatar.personaKey} 
                    className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px] overflow-hidden"
                    data-testid={`card-preset-${avatar.personaKey}`}
                  >
                    <div className="h-48 relative overflow-hidden">
                      <img 
                        src={avatar.imageUrl} 
                        alt={avatar.name}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent" />
                      <div className="absolute bottom-4 left-4 right-4">
                        <h3 className="text-xl font-bold text-white">{avatar.name}</h3>
                        {avatar.personalityProfile.backstory && (
                          <p className="text-sm text-white/80 italic mt-1">
                            "{avatar.personalityProfile.backstory.split('.')[0]}."
                          </p>
                        )}
                      </div>
                    </div>
                    <CardContent className="pt-4">
                      <p className="text-sm text-muted-foreground mb-3">
                        {avatar.personalityProfile.backstory}
                      </p>
                      <div className="flex flex-wrap gap-2 mb-3">
                        {avatar.personalityProfile.traits.map((trait: string) => (
                          <span key={trait} className="text-xs px-2 py-1 bg-primary/10 text-primary rounded-full">
                            {trait}
                          </span>
                        ))}
                      </div>
                      <div className="space-y-2 text-sm">
                        <div className="flex items-center justify-between">
                          <span className="text-muted-foreground">Trading Style:</span>
                          <span className="text-foreground capitalize font-medium">
                            {avatar.personalityProfile.tradingStyle}
                          </span>
                        </div>
                        {avatar.personalityProfile.catchphrase && (
                          <div className="pt-2 border-t border-white/10">
                            <p className="italic text-primary/80 text-xs">
                              "{avatar.personalityProfile.catchphrase}"
                            </p>
                          </div>
                        )}
                      </div>
                      <Button 
                        className="w-full mt-4 rounded-[28px]"
                        onClick={(e) => {
                          e.stopPropagation();
                          selectAvatar.mutate(avatar.personaKey);
                        }}
                        disabled={selectAvatar.isPending}
                        data-testid={`button-select-${avatar.personaKey}`}
                      >
                        Select {avatar.name.split(' ')[0]}
                      </Button>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>

          <TabsContent value="custom" className="space-y-6 mt-6">
            <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px]">
              <CardHeader>
                <CardTitle>Design Your AI Advisor</CardTitle>
                <CardDescription>
                  Create a custom avatar with unique personality traits
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="avatar-image">Avatar Image</Label>
                  <div className="mt-1 space-y-2">
                    {imagePreview ? (
                      <div className="relative w-32 h-32 mx-auto">
                        <img 
                          src={imagePreview} 
                          alt="Avatar preview" 
                          className="w-full h-full rounded-full object-cover border-2 border-white/20"
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute -top-2 -right-2 rounded-full h-8 w-8 p-0"
                          onClick={() => {
                            setAvatarImage(null);
                            setImagePreview("");
                            if (fileInputRef.current) {
                              fileInputRef.current.value = "";
                            }
                          }}
                        >

                        </Button>
                      </div>
                    ) : (
                      <div 
                        onClick={() => fileInputRef.current?.click()}
                        onDragEnter={handleDragEnter}
                        onDragLeave={handleDragLeave}
                        onDragOver={handleDragOver}
                        onDrop={handleDrop}
                        className={`w-32 h-32 mx-auto border-2 border-dashed rounded-full flex flex-col items-center justify-center cursor-pointer transition-all ${
                          isDragging 
                            ? 'border-primary bg-primary/10 scale-105' 
                            : 'border-white/20 hover:border-white/40'
                        }`}
                      >
                        <Upload className={`w-8 h-8 mb-2 transition-colors ${
                          isDragging ? 'text-primary' : 'text-white/40'
                        }`} />
                        <span className={`text-xs transition-colors ${
                          isDragging ? 'text-primary font-semibold' : 'text-white/60'
                        }`}>
                          {isDragging ? 'Drop image here' : 'Upload Image'}
                        </span>
                      </div>
                    )}
                    <input
                      ref={fileInputRef}
                      type="file"
                      accept="image/*"
                      onChange={handleFileSelect}
                      className="hidden"
                      data-testid="input-avatar-image"
                    />
                    <p className="text-xs text-muted-foreground text-center">
                      JPEG, PNG, or GIF up to 5MB  Click or drag & drop
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName" className="text-white">First Name *</Label>
                    <Input 
                      id="firstName"
                      value={firstName}
                      onChange={(e) => setFirstName(e.target.value.slice(0, 20))}
                      placeholder="e.g., Morgan"
                      maxLength={20}
                      className="mt-1 !bg-white/5 border-white/10 !text-white placeholder:!text-white/40 focus:!bg-white/10"
                      style={{ backgroundColor: 'rgba(255,255,255,0.05)', color: 'white' }}
                      data-testid="input-first-name"
                    />
                    <p className="text-xs text-muted-foreground mt-1">
                      {firstName.length}/20 characters
                    </p>
                  </div>

                  <div>
                    <Label htmlFor="lastName" className="text-white">Last Name (Optional)</Label>
                    <Input 
                      id="lastName"
                      value={lastName}
                      onChange={(e) => setLastName(e.target.value.slice(0, 20))}
                      placeholder="e.g., Blake"
                      maxLength={20}
                      className="mt-1 !bg-white/5 border-white/10 !text-white placeholder:!text-white/40 focus:!bg-white/10"
                      style={{ backgroundColor: 'rgba(255,255,255,0.05)', color: 'white' }}
                      data-testid="input-last-name"
                    />
                    <p className="text-xs text-muted-foreground mt-1">
                      {lastName.length}/20 characters
                    </p>
                  </div>
                </div>

                <div>
                  <Label htmlFor="personality" className="text-white">Personality Description</Label>
                  <Textarea
                    id="personality"
                    value={personality}
                    onChange={(e) => setPersonality(e.target.value)}
                    placeholder="Describe their personality and background. Example: Former hedge fund manager who made millions in the 2008 crash. Speaks with confidence and uses market metaphors. Loves to share war stories from trading floors..."
                    className="mt-1 !bg-white/5 border-white/10 !text-white placeholder:!text-white/40 focus:!bg-white/10"
                    style={{ backgroundColor: 'rgba(255,255,255,0.05)', color: 'white' }}
                    rows={4}
                    data-testid="input-personality-traits"
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    This will shape how your advisor speaks to you - their jokes, greetings, and investing philosophy
                  </p>
                </div>

                <div>
                  <Label className="text-white font-medium">Trading Style</Label>
                  <RadioGroup value={tradingStyle} onValueChange={setTradingStyle} className="mt-2 space-y-2">
                    <div className="flex items-center space-x-2 p-2 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <RadioGroupItem value="conservative" id="conservative" className="border-white/40 text-primary" />
                      <Label htmlFor="conservative" className="text-white font-normal cursor-pointer">Conservative - Focus on stability</Label>
                    </div>
                    <div className="flex items-center space-x-2 p-2 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <RadioGroupItem value="balanced" id="balanced" className="border-white/40 text-primary" />
                      <Label htmlFor="balanced" className="text-white font-normal cursor-pointer">Balanced - Mix of growth and safety</Label>
                    </div>
                    <div className="flex items-center space-x-2 p-2 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <RadioGroupItem value="aggressive" id="aggressive" className="border-white/40 text-primary" />
                      <Label htmlFor="aggressive" className="text-white font-normal cursor-pointer">Aggressive - High risk, high reward</Label>
                    </div>
                    <div className="flex items-center space-x-2 p-2 rounded-lg bg-white/5 hover:bg-white/10 transition-colors">
                      <RadioGroupItem value="analytical" id="analytical" className="border-white/40 text-primary" />
                      <Label htmlFor="analytical" className="text-white font-normal cursor-pointer">Analytical - Data-driven decisions</Label>
                    </div>
                  </RadioGroup>
                </div>

                <div>
                  <Label htmlFor="appearance" className="text-white">Visual Style (Optional)</Label>
                  <Input
                    id="appearance"
                    value={appearance}
                    onChange={(e) => setAppearance(e.target.value)}
                    placeholder="e.g., professional, modern, approachable"
                    className="mt-1 !bg-white/5 border-white/10 !text-white placeholder:!text-white/40 focus:!bg-white/10"
                    style={{ backgroundColor: 'rgba(255,255,255,0.05)', color: 'white' }}
                    data-testid="input-visual-style"
                  />
                </div>

                <Button 
                  onClick={() => createCustom.mutate({ 
                    firstName, 
                    lastName,
                    personality, 
                    tradingStyle, 
                    appearance 
                  })}
                  disabled={!firstName || !personality || createCustom.isPending}
                  className="w-full rounded-[28px]"
                  style={{ position: 'relative', zIndex: 20 }}
                  data-testid="button-create-custom-avatar"
                >
                  <Sparkles className="w-4 h-4 mr-2" />
                  Create Custom Avatar
                </Button>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="history" className="space-y-4 mt-6">
            {history?.length === 0 ? (
              <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px] text-center py-8">
                <CardContent>
                  <p className="text-muted-foreground">
                    No custom avatars created yet
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-2 gap-4">
                {history?.map((item: any) => (
                  <Card 
                    key={item.id} 
                    className="bg-white/5 border-white/10 hover-elevate cursor-pointer backdrop-blur-xl rounded-[28px]"
                    onClick={() => selectAvatar.mutate(item.avatarId)}
                    data-testid={`card-history-${item.id}`}
                  >
                    <CardHeader>
                      <CardTitle className="text-lg">{item.avatar?.name}</CardTitle>
                      <CardDescription className="text-sm">
                        Created {new Date(item.createdAt).toLocaleDateString()}
                      </CardDescription>
                    </CardHeader>
                    {item.isActive && (
                      <CardContent>
                        <div className="flex items-center gap-2 text-primary">
                          <Check className="w-4 h-4" />
                          <span className="text-sm">Currently Active</span>
                        </div>
                      </CardContent>
                    )}
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/LeaderboardPage.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Trophy, TrendingUp, Target, Medal, Crown, Award, ChevronUp, ChevronDown, Minus } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import Navigation from "@/components/Navigation";
import NavigationBreadcrumbs from "@/components/NavigationBreadcrumbs";
import { ProtectedRoute } from "@/lib/auth";

interface Trader {
  id: string;
  username: string;
  avatar?: string;
  totalReturn: number;
  sharpeRatio: number;
  winRate: number;
  totalTrades: number;
  riskScore: number;
  achievementPoints: number;
  rank: number;
  previousRank: number;
  streak?: number;
}

function LeaderboardPage() {
  const [timeframe, setTimeframe] = useState("monthly");
  const [category, setCategory] = useState("overall");

  // Mock data for demonstration
  const mockTraders: Trader[] = [
    {
      id: "1",
      username: "AlphaTrader",
      totalReturn: 45.8,
      sharpeRatio: 2.3,
      winRate: 68,
      totalTrades: 234,
      riskScore: 8.5,
      achievementPoints: 15420,
      rank: 1,
      previousRank: 2,
      streak: 7
    },
    {
      id: "2",
      username: "MarketMaven",
      totalReturn: 38.2,
      sharpeRatio: 2.8,
      winRate: 72,
      totalTrades: 189,
      riskScore: 6.2,
      achievementPoints: 13850,
      rank: 2,
      previousRank: 1,
      streak: 5
    },
    {
      id: "3",
      username: "RiskReward",
      totalReturn: 32.5,
      sharpeRatio: 3.1,
      winRate: 65,
      totalTrades: 156,
      riskScore: 5.8,
      achievementPoints: 12300,
      rank: 3,
      previousRank: 4,
      streak: 3
    },
    {
      id: "4",
      username: "TechBull",
      totalReturn: 28.9,
      sharpeRatio: 1.9,
      winRate: 61,
      totalTrades: 298,
      riskScore: 7.2,
      achievementPoints: 11500,
      rank: 4,
      previousRank: 3,
    },
    {
      id: "5",
      username: "ValueHunter",
      totalReturn: 25.4,
      sharpeRatio: 2.5,
      winRate: 70,
      totalTrades: 134,
      riskScore: 4.5,
      achievementPoints: 10800,
      rank: 5,
      previousRank: 6,
    }
  ];

  const getRankBadge = (rank: number) => {
    switch (rank) {
      case 1:
        return <Crown className="w-6 h-6 text-yellow-500" />;
      case 2:
        return <Medal className="w-6 h-6 text-gray-400" />;
      case 3:
        return <Medal className="w-6 h-6 text-amber-600" />;
      default:
        return <span className="text-lg font-bold text-muted-foreground">#{rank}</span>;
    }
  };

  const getRankChange = (current: number, previous: number) => {
    const change = previous - current;
    if (change > 0) {
      return (
        <span className="flex items-center text-green-500 text-xs font-medium">
          <ChevronUp className="w-3 h-3" />
          {change}
        </span>
      );
    } else if (change < 0) {
      return (
        <span className="flex items-center text-red-500 text-xs font-medium">
          <ChevronDown className="w-3 h-3" />
          {Math.abs(change)}
        </span>
      );
    }
    return (
      <span className="flex items-center text-muted-foreground text-xs">
        <Minus className="w-3 h-3" />
      </span>
    );
  };

  const getCategoryLabel = (cat: string) => {
    switch (cat) {
      case "overall": return "Overall Performance";
      case "risk-adjusted": return "Risk-Adjusted Returns";
      case "consistency": return "Most Consistent";
      case "volume": return "Most Active";
      case "achievements": return "Achievement Points";
      default: return "Overall Performance";
    }
  };

  return (
    <div className="min-h-screen bg-black">
      <Navigation />
      <NavigationBreadcrumbs />
      <div className="max-w-[1600px] mx-auto px-6 sm:px-10 lg:px-16 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-foreground mb-2">
            Global Leaderboards
          </h1>
          <p className="text-muted-foreground font-normal">
            Compete with traders worldwide and climb the ranks
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
          <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px]">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground font-medium">Total Traders</p>
                  <p className="text-2xl font-bold text-foreground">12,847</p>
                </div>
                <Trophy className="w-8 h-8 text-primary opacity-50" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px]">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground font-medium">Avg. Sharpe Ratio</p>
                  <p className="text-2xl font-bold text-foreground">1.85</p>
                </div>
                <Target className="w-8 h-8 text-primary opacity-50" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px]">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground font-medium">Your Rank</p>
                  <p className="text-2xl font-bold text-foreground">#127</p>
                </div>
                <Award className="w-8 h-8 text-primary opacity-50" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-primary/20 border-primary/30 backdrop-blur-xl rounded-[28px]">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-primary/80 font-medium">Prize Pool</p>
                  <p className="text-2xl font-bold text-primary">$25,000</p>
                </div>
                <TrendingUp className="w-8 h-8 text-primary opacity-50" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Leaderboard */}
        <Card className="bg-white/5 border-white/10 backdrop-blur-xl rounded-[28px]">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl font-semibold">Top Traders</CardTitle>
                <CardDescription className="font-normal">
                  Rankings based on {getCategoryLabel(category)}
                </CardDescription>
              </div>

              {/* Timeframe Selector */}
              <Tabs value={timeframe} onValueChange={setTimeframe}>
                <TabsList className="bg-white/5">
                  <TabsTrigger value="daily" className="font-medium">Daily</TabsTrigger>
                  <TabsTrigger value="weekly" className="font-medium">Weekly</TabsTrigger>
                  <TabsTrigger value="monthly" className="font-medium">Monthly</TabsTrigger>
                  <TabsTrigger value="all-time" className="font-medium">All Time</TabsTrigger>
                </TabsList>
              </Tabs>
            </div>
          </CardHeader>

          <CardContent className="p-0">
            {/* Category Tabs */}
            <div className="px-6 pb-4">
              <Tabs value={category} onValueChange={setCategory}>
                <TabsList className="bg-white/5 w-full">
                  <TabsTrigger value="overall" className="flex-1 font-medium">Overall</TabsTrigger>
                  <TabsTrigger value="risk-adjusted" className="flex-1 font-medium">Risk-Adjusted</TabsTrigger>
                  <TabsTrigger value="consistency" className="flex-1 font-medium">Consistency</TabsTrigger>
                  <TabsTrigger value="volume" className="flex-1 font-medium">Most Active</TabsTrigger>
                  <TabsTrigger value="achievements" className="flex-1 font-medium">Achievements</TabsTrigger>
                </TabsList>
              </Tabs>
            </div>

            {/* Leaderboard Table */}
            <div className="border-t border-white/10">
              {mockTraders.map((trader, index) => (
                <div
                  key={trader.id}
                  className={`flex items-center justify-between p-6 hover:bg-white/5 transition-colors ${
                    index !== mockTraders.length - 1 ? "border-b border-white/10" : ""
                  }`}
                  data-testid={`leaderboard-row-${trader.id}`}
                >
                  <div className="flex items-center gap-4">
                    {/* Rank */}
                    <div className="w-12 flex items-center justify-center">
                      {getRankBadge(trader.rank)}
                    </div>

                    {/* Rank Change */}
                    <div className="w-8">
                      {getRankChange(trader.rank, trader.previousRank)}
                    </div>

                    {/* User Info */}
                    <div className="flex items-center gap-3">
                      <Avatar className="w-10 h-10">
                        <AvatarFallback className="bg-primary/20 text-primary font-medium">
                          {trader.username.slice(0, 2).toUpperCase()}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <div className="flex items-center gap-2">
                          <p className="font-semibold text-foreground">{trader.username}</p>
                          {trader.streak && trader.streak >= 5 && (
                            <Badge variant="secondary" className="text-xs font-medium">
                               {trader.streak} day streak
                            </Badge>
                          )}
                        </div>
                        <p className="text-xs text-muted-foreground font-normal">
                          {trader.totalTrades} trades  {trader.winRate}% win rate
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Stats */}
                  <div className="flex items-center gap-8">
                    <div className="text-right">
                      <p className="text-xs text-muted-foreground font-medium">Return</p>
                      <p className={`font-bold ${trader.totalReturn >= 0 ? 'text-green-500' : 'text-red-500'}`}>
                        {trader.totalReturn >= 0 ? '+' : ''}{trader.totalReturn}%
                      </p>
                    </div>

                    <div className="text-right">
                      <p className="text-xs text-muted-foreground font-medium">Sharpe Ratio</p>
                      <p className="font-bold text-foreground">
                        {trader.sharpeRatio.toFixed(1)}
                      </p>
                    </div>

                    <div className="text-right">
                      <p className="text-xs text-muted-foreground font-medium">Risk Score</p>
                      <p className="font-bold text-foreground">
                        {trader.riskScore.toFixed(1)}
                      </p>
                    </div>

                    <div className="text-right">
                      <p className="text-xs text-muted-foreground font-medium">Points</p>
                      <p className="font-bold text-primary">
                        {trader.achievementPoints.toLocaleString()}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Competition Banner */}
        <Card className="mt-8 bg-gradient-to-r from-primary/20 to-purple-600/20 border-primary/30 backdrop-blur-xl rounded-[28px]">
          <CardContent className="p-8">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-2xl font-bold text-foreground mb-2">
                  Monthly Trading Competition
                </h3>
                <p className="text-muted-foreground font-normal mb-4">
                  Compete for prizes with risk-adjusted performance metrics
                </p>
                <div className="flex items-center gap-4">
                  <Badge variant="secondary" className="font-medium">
                    15 days remaining
                  </Badge>
                  <span className="text-sm text-muted-foreground font-normal">
                    Entry: Free for all users
                  </span>
                </div>
              </div>
              <Trophy className="w-24 h-24 text-primary opacity-30" />
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

function LeaderboardPageContent() {
  return <LeaderboardPage />;
}

export default function LeaderboardPageWrapper() {
  return (
    <ProtectedRoute>
      <LeaderboardPageContent />
    </ProtectedRoute>
  );
}

client/src/components/QuickActions.tsx.content:
import { Button } from "@/components/ui/button";
import { ShoppingCart, TrendingUp, Brain, LineChart } from "lucide-react";
import { useLocation } from "wouter";

export default function QuickActions() {
  const [, setLocation] = useLocation();

  const actions = [
    {
      icon: ShoppingCart,
      label: "Trade",
      onClick: () => setLocation('/trades'),
      color: 'text-green-400',
      bgColor: 'bg-green-400/10 hover:bg-green-400/20'
    },
    {
