import { createContext, useContext, useState, ReactNode } from 'react';
import { StockDetailModalPro } from '@/components/StockDetailModalPro';

interface StockDetailModalContextType {
  openModal: (symbol: string) => void;
  closeModal: () => void;
}

const StockDetailModalContext = createContext<StockDetailModalContextType | undefined>(undefined);

export function useStockDetailModal() {
  const context = useContext(StockDetailModalContext);
  if (!context) {
    throw new Error('useStockDetailModal must be used within a StockDetailModalProvider');
  }
  return context;
}

interface StockDetailModalProviderProps {
  children: ReactNode;
  onTrade?: (action: 'buy' | 'sell', symbol: string) => void;
}

export function StockDetailModalProvider({ children, onTrade }: StockDetailModalProviderProps) {
  const [selectedStock, setSelectedStock] = useState<string | null>(null);
  const [isOpen, setIsOpen] = useState(false);

  const openModal = (symbol: string) => {
    setSelectedStock(symbol);
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
    setSelectedStock(null);
  };

  return (
    <StockDetailModalContext.Provider value={{ openModal, closeModal }}>
      {children}
      {selectedStock && (
        <StockDetailModalPro
          symbol={selectedStock}
          open={isOpen}
          onOpenChange={setIsOpen}
          onTrade={onTrade}
        />
      )}
    </StockDetailModalContext.Provider>
  );
}

client/src/components/TickerLink.tsx.content:
import { cn } from "@/lib/utils";
import { useStockDetailModal } from "@/contexts/StockDetailModalContext";

interface TickerLinkProps {
  symbol: string;
  className?: string;
  showDollarSign?: boolean;
  variant?: 'default' | 'badge' | 'inline' | 'large';
  children?: React.ReactNode;
}

export function TickerLink({ 
  symbol, 
  className, 
  showDollarSign = false,
  variant = 'default',
  children 
}: TickerLinkProps) {
  const { openModal } = useStockDetailModal();

  const baseStyles = "text-primary hover:text-primary/80 underline underline-offset-2 cursor-pointer font-medium";

  const variantStyles = {
    default: "text-sm",
    badge: "px-2 py-0.5 bg-primary/10 rounded-full text-xs",
    inline: "text-inherit",
    large: "text-base"
  };

  const handleClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    openModal(symbol);
  };

  return (
    <button
      onClick={handleClick}
      className={cn(baseStyles, variantStyles[variant], className)}
      data-testid={`ticker-${symbol}`}
      type="button"
    >
      {showDollarSign && '$'}{children || symbol}
    </button>
  );
}

